INSERT INTO `content_blocks` (`id`, `owner_id`, `type`, `region`, `value_text`, `value_number`, `value_money`, `value_media_id`, `is_published`, `created`, `modified`, `i18n_value_text_en`)
VALUES
	(1, 1, 'richtext', 'home.fields.support', '							<p>\n								Die freie Enzyklopädie Wikipedia wird von ehrenamtlichen Autorinnen und Autoren\n								geschrieben. Da es keine hauptamtliche Redaktion gibt, lebt die Wikipedia von der\n								Mitarbeit möglichst vieler Menschen. Aber die Zahl der aktiven Autorinnen und Autoren\n								geht zurück. Wenn sich auch in Zukunft immer weniger Menschen engagieren, könnten\n								Artikel nicht mehr aktuell gehalten werden – oder keine neuen entstehen.\n							</p>\n							<p>\n								Wir setzen uns deshalb dafür ein, dass die Wikipedia auch in Zukunft verlässlich,\n								umfassend und aktuell ist. Wir machen Menschen darauf aufmerksam, dass sie sich an der\n								Wikipedia beteiligen können und bieten Interessierten Hilfe und Einstiegsmöglichkeiten.\n							</p>\n							<p>\n								Alle Autorinnen und Autoren – ob neu oder schon langjährig aktiv – bestärken wir in\n								ihrem Engagement und stehen ihnen zur Seite, wenn es darum geht, ihre Pläne in die\n								Tat umzusetzen. Wir vergeben zum Beispiel Stipendien, helfen bei der Durchführung\n								von Workshops und Wettbewerben oder bieten technische Unterstützung.\n							</p>', NULL, NULL, NULL, 1, '2018-02-19 13:24:27', '2018-02-19 13:24:27', '<p>ads</p>'),
	(2, 1, 'richtext', 'home.fields.dev', '\n							<p>\n								Wenn wir heute etwas wissen wollen, schauen wir einfach im Internet nach. Anders als bei\n								einem gedruckten Lexikon brauchen wir für das Internet Software, damit dieses Wissen\n								überhaupt vernünftig angezeigt werden kann. Aber die digitale Umgebung entwickelt sich\n								rasant weiter.\n							</p>\n							<p>\n								Damit die Wikipedia auch in Zukunft benutzerfreundlich und für alle digital zugänglich\n								bleibt, bauen wir MediaWiki, die Software hinter der Wikipedia, immer weiter aus.\n							</p>\n							<p>\n								Außerdem entwickeln wir unsere freie Datenbank Wikidata, die Menschen auf der ganzen\n								Welt zum Beispiel an die fast 300 verschiedenen Sprachversionen von Wikipedia anbinden\n								können. Die Software hinter Wikidata ist so konzipiert, dass sie jederzeit und von allen\n								Interessierten auch außerhalb der Wikimedia-Welt Zugang zu unserer Datenbank bietet.\n							</p>\n', NULL, NULL, NULL, 1, '2018-02-19 13:24:35', '2018-02-19 13:24:35', ''),
	(3, 1, 'richtext', 'home.fields.know', '\n							<p>\n								Die Welt ist reich an Kulturgütern wie Büchern, Gemälden oder wissenschaftlichen\n								Arbeiten. Sie können von der Öffentlichkeit meist angesehen, aber nicht genutzt werden.\n								Selbst wenn sie beispielsweise in einem Museum ausgestellt sind, dürfen wir sie oft\n								nicht fotografieren und verwenden.\n							</p>\n							<p>\n								Öffentliche Güter gehören aus unserer Sicht in die Öffentlichkeit. Wir arbeiten daher mit\n								Bildungs-, Wissenschafts- und Kulturinstitutionen zusammen und befähigen sie, ihre Inhalte\n								unter eine freie Lizenz zu stellen. So können all diese Schätze frei weiterverwendet\n								werden – etwa für Unterrichtsmaterialien oder einen Blog.\n							</p>\n							<p>\n								Dafür braucht es einen geeigneten rechtlichen Rahmen. Wir wollen erreichen, dass Menschen\n								so leicht wie möglich ihr Wissen teilen können und es gemeinschaftlich genutzt werden\n								kann. Wir setzen uns aus diesem Grund für ein zeitgemäßes Urheberrecht ein, das die\n								Möglichkeiten des Internets nutzt, um Wissen zu verbreiten und zu vermehren.\n							</p>\n		', NULL, NULL, NULL, 1, '2018-02-19 13:24:38', '2018-02-19 13:24:38', '');

	INSERT INTO `content_blocks` (`id`, `owner_id`, `type`, `region`, `value_text`, `value_number`, `value_money`, `value_media_id`, `is_published`, `created`, `modified`, `i18n_value_text_en`)
VALUES
	(4, 1, 'richtext', 'home.vision', '				Unsere <strong>Vision</strong> ist eine Welt, in der\n				jeder Mensch am Wissen der Mensch­heit teilhaben, es nutzen und mehren\n				kann.\n', NULL, NULL, NULL, 1, '2018-02-19 13:56:53', '2018-02-19 13:56:53', ''),
	(5, 1, 'richtext', 'home.mission', '\n				Wir setzen uns für Freies Wissen ein, um die Chancen­gleichheit beim Zu­gang zu Wissen\n				und Bildung zu fördern.\n', NULL, NULL, NULL, 1, '2018-02-19 14:02:46', '2018-02-19 14:02:49', '');
-- Rename tags in TABLE 'tags'
UPDATE `tags` SET `name` = 'wikimedia' WHERE `name` = 'wmde';
UPDATE `tags` SET `name` = 'mitglieder' WHERE `name` = 'member';
UPDATE `tags` SET `name` = 'projekt' WHERE `name` = 'project';
-- Rename tags in TABLE 'posts' 
UPDATE `posts` SET `tags` = REPLACE(`tags`,  'wmde', 'wikimedia');
UPDATE `posts` SET `tags` = REPLACE(`tags`,  'member', 'mitglieder');
UPDATE `posts` SET `tags` = REPLACE(`tags`,  'project', 'projekt');
INSERT INTO `pages` (`id`, `owner_id`, `site`, `title`, `body`, `is_published`, `created`, `modified`, `i18n_title_en`, `i18n_body_en`)
VALUES
	(3, 1, NULL, 'Impressum und Kontakt', '<p>\r\n			Dieses Impressum gilt für die Domains wikimedia.de und wikipedia.de. Bitte beachten Sie, dass Wikimedia Deutschland nicht der Diensteanbieter aller auf dieser Website genannten Wikimedia-Projekte ist.<br>\r\n		</p>\r\n\r\n		<h3 class=\"rt__h--gamma rt__h--beta\">\r\n			Diensteanbieter\r\n		</h3>\r\n\r\n		<p>\r\n			Wikimedia Deutschland – Gesellschaft zur Förderung Freien Wissens e. V.<br>\r\n			Tempelhofer Ufer 23/24<br>\r\n			10963 Berlin\r\n		</p>\r\n\r\n		<p>\r\n			E-Mail: <a href=\"mailto:info@wikimedia.de\">info@wikimedia.de</a><br>\r\n			Telefon: +49 (0)30-219 15 826-0<br>\r\n			Fax: +49 (0)30-219 158 26-9\r\n		</p>\r\n\r\n		<p>\r\n			Geschäftsführender Vorstand: Abraham Taherivand<br>\r\n			Eingetragen im Vereinsregister des Amtsgerichts Charlottenburg, VR 23855. Inhaltlich Verantwortlicher gemäß § 55 Abs. 2 RStV: Abraham Taherivand (Anschrift wie oben).\r\n		</p>\r\n\r\n		<h3 class=\"rt__h--gamma rt__h--beta\">\r\n			Datenschutzerklärung\r\n		</h3>\r\n\r\n		<p>\r\n			Personenbezogene Daten werden auf dieser Webseite nur im technisch notwendigen Umfang erhoben. In keinem Fall werden die erhobenen Daten verkauft oder aus anderen Gründen an Dritte weitergegeben. Im Folgenden erhalten Sie einen Überblick über die von uns im Rahmen dieser Website erhobenen Daten.<br>\r\n		</p>\r\n\r\n		<ul>\r\n			<li>Log-Files: Bei der Nutzung dieses Angebots werden die von Ihrem Browser an den Server übermittelten Daten erfasst und gespeichert. Dies umfasst üblicherweise Informationen über den Typ und die Version des von Ihnen verwendeten Browsers, das verwendete Betriebssystem, die Referrer URL (die Webseite, von der aus Sie zu dieser Website gelangt sind), den Hostnamen des zugreifenden Rechners (die IP Adresse) sowie die Uhrzeit der Serveranfrage. In der Regel lassen sich diese Daten nicht bestimmten Personen – und damit auch nicht Ihnen – zuordnen. Weder wird zu diesem Zweck ein Abgleich der Daten mit anderen Daten vorgenommen, noch werden sie hierfür mit anderen Daten zusammengeführt. Die Daten werden regelmäßig nach einer statistischen Auswertung gelöscht.<br>\r\n			</li>\r\n\r\n			<li>Cookies: Diese Website verwendet an mehreren Stellen Cookies. Cookies sind kleine Textdateien, die Ihr Browser speichert und die dazu dienen, die Benutzung einer Website einfacher, effektiver und sicherer zu machen. Cookies richten auf Ihrem Rechner keinen Schaden an, nehmen minimalen Speicherplatz in Anspruch und enthalten keine Viren.<br>\r\n			</li>\r\n\r\n			<li>Google Analytics: Diese Website benutzt Google Analytics, einen Webanalysedienst der Google Inc. („Google“). Google Analytics verwendet sog. „Cookies“, Textdateien, die auf Ihrem Computer gespeichert werden und die eine Analyse der Benutzung der Website durch Sie ermöglichen. Die durch das Cookie erzeugten Informationen über Ihre Benutzung dieser Website werden in der Regel an einen Server von Google in den USA übertragen und dort gespeichert. Im Falle der Aktivierung der IP-Anonymisierung auf dieser Website, wird Ihre IP-Adresse von Google jedoch innerhalb von Mitgliedstaaten der Europäischen Union oder in anderen Vertragsstaaten des Abkommens über den Europäischen Wirtschaftsraum zuvor gekürzt. Nur in Ausnahmefällen wird die volle IP-Adresse an einen Server von Google in den USA übertragen und dort gekürzt. Im Auftrag des Betreibers dieser Website wird Google diese Informationen benutzen, um Ihre Nutzung der Website auszuwerten, um Reports über die Websiteaktivitäten zusammenzustellen und um weitere mit der Websitenutzung und der Internetnutzung verbundene Dienstleistungen gegenüber dem Websitebetreiber zu erbringen. Die im Rahmen von Google Analytics von Ihrem Browser übermittelte IP-Adresse wird nicht mit anderen Daten von Google zusammengeführt. Sie können die Speicherung der Cookies durch eine entsprechende Einstellung Ihrer Browser-Software verhindern; wir weisen Sie jedoch darauf hin, dass Sie in diesem Fall gegebenenfalls nicht sämtliche Funktionen dieser Website vollumfänglich werden nutzen können. Sie können darüber hinaus die Erfassung der durch das Cookie erzeugten und auf Ihre Nutzung der Website bezogenen Daten (inkl. Ihrer IP-Adresse) an Google sowie die Verarbeitung dieser Daten durch Google verhindern, indem Sie das unter dem folgenden Link (<a href=\"http://tools.google.com/dlpage/gaoptout?hl=de\">http://tools.google.com/dlpage/gaoptout?hl=de</a>) verfügbare Browser-Plugin herunterladen und installieren. Sie können die Erfassung durch Google Analytics verhindern, indem Sie auf folgenden Link klicken. Es wird ein Opt-Out-Cookie gesetzt, das die zukünftige Erfassung Ihrer Daten beim Besuch dieser Website verhindert: <a href=\"javascript:gaOptout()\">Google Analytics deaktivieren</a>&nbsp;Nähere Informationen zu Nutzungsbedingungen und Datenschutz finden Sie unter <a href=\"http://www.google.com/analytics/terms/de.html\">http://www.google.com/analytics/terms/de.html</a>&nbsp;bzw. unter <a href=\"https://www.google.de/intl/de/policies/\">https://www.google.de/intl/de/policies/</a>. Wir weisen Sie darauf hin, dass auf dieser Website Google Analytics um den Code „anonymizeIp“ erweitert wurde, um eine anonymisierte Erfassung von IP-Adressen (sog. IP-Masking) zu gewährleisten.<br>\r\n			</li>\r\n		</ul>\r\n\r\n		<h3 class=\"rt__h--gamma rt__h--beta\">\r\n			Büro\r\n		</h3>\r\n\r\n		<p>\r\n			Wikimedia Deutschland<br>\r\n			Gesellschaft zur Förderung Freien Wissens e. V.<br>\r\n			Postfach 61 03 49<br>\r\n			10925 Berlin<br>\r\n		</p>\r\n\r\n		<p>\r\n			Telefon: +49 (0)30-219 158 26-0<br>\r\n			Fax: +49 (0)30-219 158 26-9<br>\r\n			E-Mail: <a href=\"mailto:info@wikimedia.de\">info@wikimedia.de</a>\r\n		</p>', 1, '2018-03-06 11:43:24', '2018-03-06 12:28:14', 'Imprint and Contact', '<p>impressum english<br></p>');

